import React from 'react';
import { Card, CardBody } from 'reactstrap';
import {Link} from 'react-router-dom';

const TourCard = ({ tour }) => {

  const { id, title, photo, price, featured, avgRating, reviews } = tour;

  return (
    <div className='tour_card'>
        <Card>
            <div className="tour_img">
                <img src={photo} alt='tour' />
            </div>
            <CardBody>
                <h5 className='tour_title'>
                    <Link to={`/tours/${id}`}>{title}</Link>
                </h5>
                <div className="card_bottom d-flex align-items-center justify-content-between">
                    <span className='tour_price'>${price}</span>
                    <span className='tour_rating d-flex align-items-center'>
                        <i className="ri-star-fill" style={{ color: 'var(--secondary-color)' }}></i> {avgRating === 0 ? 'Not Rated' : avgRating} ({reviews} reviews)
                    </span>
                </div>
            </CardBody>
        </Card>
    </div>
  );
};

export default TourCard;
